CASE pushout_crab_state OF
	0 : 
		if start_pushout_crab_handle = True then

				
				Y1:=true;
				Y2:=true;
				
				//выдвигаем лапы
				Y16:=false;
				Y17:=true;
				
				Y18:=false;//жесткий режим (не плавающий)
				pushout_crab_state:=1;
				start_pushout_crab_handle := false;
		end_if;
		
	1 : 
			Y1:=False;
			Y2:=False;
			pushout_crab_state:=2;
	2:;
		if SQ45 and 
			SQ46 and 
			SQ47 and 
			SQ48 	 then ;  
			
			//фиксируем лапы
			Y16:=false;
			Y17:=false;

			pushout_crab_state:=3;
		end_if;
		
	3:;

		pushout_crab_state:=99;


	
	99 :; // Processing after normal end
	  //up_frame_cycle_state:=INT#0;
	98 :; // Processing after error end.
		//
END_CASE;
