StructuredTextModel.Text:
;;
;
CASE down_crab_cycle_state OF
	0 : 
		if start_down_crab_cycle_handle = True then
			Y3:=True; //режим пружины
			Y6:=true; //верхний разжат
			Y7:=true; //нижний сжат
			
			liftmech.cmd := LIFT_CMD#stop;
			start_down_crab_cycle_handle := false;
			down_crab_cycle_state:=1;
			
			last_max_diff:= 30;
			err_cnt:=0;
		end_if;
		
	1 :;
		if (SQ_DW=16#55555555 ) then ;  
			Y1:=False;
			Y2:=False;

			Y3:=True; //режим пружины
			Y6:=true; //верхний разжат
			Y7:=false; //нижний разжат


			
			down_crab_cycle_state:=20;
		end_if;
	20:;
		if 	(not(SQ1 or
			SQ3 or
			SQ9 or
			SQ11 or
			SQ17 or
			SQ19 or
			SQ25 or
			SQ27) )and
			( (SQ2 and	SQ4 and	SQ10 and SQ12 and	SQ18 and SQ20 and SQ26 and SQ28)) then;
		
				liftmech.cmd := LIFT_CMD#expand;
				down_crab_cycle_state:=2;
		end_if;

	
	2:;
		if (liftmech.state = LIFT_STATE#expand_end)  
			
		
			 then ;  

			Y3:=True; //режим пружины
			Y6:=true; //верхний расжат
			Y7:=true; //нижний cжат
			
			liftmech.cmd := LIFT_CMD#reduce;
			
			down_crab_cycle_state:=3;
		end_if;
	
		
	3:;
		if 	((SQ1 and
			SQ3 and
			SQ9 and
			SQ11 and
			SQ17 and
			SQ19 and
			SQ25 and
			SQ27) and
			(not (SQ2 or	SQ4 or	SQ10 or SQ12 or	SQ18 or SQ20 or SQ26 or SQ28)) )
			or (liftmech.min_dist >=810
					and liftmech.max_dist <=880)then;

			
			Y3:=True; //режим пружины
			Y6:=false; //верхний сжат
			Y7:=true; //нижний cжат			
			

			liftmech.cmd := LIFT_CMD#stop;
			down_crab_cycle_state:=31;
		end_if;
	31:;
		if (liftmech.state = LIFT_STATE#holding) then ;
			liftmech.cmd := LIFT_CMD#expand;
			down_crab_cycle_state:=4;
		end_if;
	4:;
			if (	(SQ6 and
			SQ8 and
			SQ14 and
			SQ16 and
			SQ22 and
			SQ24 and
			SQ30 and
			SQ32)) and
			( not(SQ5 or	SQ7 or	SQ13 or SQ15 or	SQ21 or SQ23 or SQ29 or SQ31)) then;
			
				liftmech.cmd := LIFT_CMD#stop;
				down_crab_cycle_state:=41;
			end_if;
	41:;
		if liftmech.state = LIFT_STATE#holding then ;
			liftmech.cmd := LIFT_CMD#reduce;
			down_crab_cycle_state:=5;
		end_if;
	5:;
		if (liftmech.state = LIFT_STATE#reduce_end)
		 then ;  	
			
			Y3:=True; //режим пружины
			Y6:=true; //верхний разжат
			Y7:=true; //нижний cжат			
			
			liftmech.cmd := LIFT_CMD#expand;		
			
			down_crab_cycle_state:=6;
		end_if;
		
	6:;
		if (not	(SQ6 or
			SQ8 or
			SQ14 or
			SQ16 or
			SQ22 or
			SQ24 or
			SQ30 or
			SQ32)) and
			( (SQ5 and	SQ7 and	SQ13 and SQ15 and	SQ21 and SQ23 and SQ29 and SQ31)) then;
			
			
			liftmech.cmd := LIFT_CMD#stop;
			down_crab_cycle_state:=61;
		end_if;
			
	61:;
		if liftmech.state = LIFT_STATE#holding then ;
			liftmech.cmd := LIFT_CMD#reduce;
			down_crab_cycle_state:=7;
		end_if;
	7:;
	
		if (liftmech.state = LIFT_STATE#reduce_end) then ;
	
			liftmech.cmd := LIFT_CMD#stop;
			Y3:=false; //режим пружины отменить
			down_crab_cycle_state:=99;
			
		end_if;
	
	
	
	99 :; // Processing after normal end
	  //up_frame_cycle_state:=INT#0;
	98 :; // Processing after error end.
		//
END_CASE;
