StructuredTextModel.Text:
;;
;
CASE up_crab_cycle_state OF
	0 : // Начальное положение домкрата
		if start_up_crab_cycle_handle = True then
			Y3:=True; //режим пружины
			Y6:=true; //верхний разжат
			Y7:=true; //нижний сжат
			
			liftmech.cmd := LIFT_CMD#stop;

			start_up_crab_cycle_handle := false;
			up_crab_cycle_state:=1;
		end_if;
		
	1 : //Подъем верхнего замка на 80 мм
		if (SQ_DW=16#55555555 ) then ;  
			Y1:=False;
			Y2:=False;
			
			liftmech.cmd := LIFT_CMD#expand;

			
			up_crab_cycle_state:=2;
		end_if;
	2:;
		if (liftmech.min_dist >= 940) 
			and (SQ_DW=16#55555555 ) 
			then ;  

			liftmech.cmd := LIFT_CMD#stop;
			up_crab_cycle_state := 21;
		end_if;
		
	21:;
		if liftmech.state = LIFT_STATE#holding then ;
			liftmech.cmd := LIFT_CMD#reduce;
			up_crab_cycle_state:=3;
		end_if;

	3:;
		if (SQ_DW=16#55555555 ) then;

			up_crab_cycle_state:=4;
		end_if;
		
		
	4:;
		if (liftmech.state = LIFT_STATE#reduce_end) then ;  	
			
			liftmech.cmd := LIFT_CMD#stop;
			up_crab_cycle_state:=5;
		end_if;
	5:;
		if (SQ_DW=16#55555555 ) then;
			Y3:=false; //режим пружины отменить

			up_crab_cycle_state:=99;
		end_if;
	6:;
	
	99 :; // Processing after normal end
	  //up_frame_cycle_state:=INT#0;
	98 :; // Processing after error end.
		//
END_CASE;
