StructuredTextModel.Text:
;
CASE up_frame_cycle_state OF
	0 : // Начальное положение домкрата
		if start_up_frame_cycle_handle = True then
			Y3:=True; //режим пружины
			Y6 :=false; //верхний сжат
			Y7:=false; //нижний расжат
			liftmech.cmd := LIFT_CMD#stop;
			
			start_up_frame_cycle_handle := false;
			up_frame_cycle_state:=1;
		end_if;
		
	1 : //Подъем верхнего замка на 80 мм
			Y1:=False;
			Y2:=False;
			liftmech.cmd := LIFT_CMD#expand;
			up_frame_cycle_state:=2;
	2:;
		if liftmech.state = LIFT_STATE#expand_end then ;  
			up_frame_cycle_state:=3;
		end_if;
		
	3:;
		if (SQ_DW=16#aaaaaaaa ) then;
			liftmech.cmd := LIFT_CMD#reduce;
			up_frame_cycle_state:=4;
		end_if;
		
		
	4:;
		if liftmech.state = LIFT_STATE#reduce_end then ;  
			up_frame_cycle_state:=5;
		end_if;
	5:;
		if (SQ_DW=16#aaaaaaaa ) then;
			Y3:=false; //режим пружины отменить
			up_frame_cycle_state:=99;
		end_if;
	6:;
	
	99 :; // Processing after normal end
	  //up_frame_cycle_state:=INT#0;
	98 :; // Processing after error end.
		//
END_CASE;
