StructuredTextModel.Text:
;
CASE init_to_hold_state OF
	0 : 
		if start_init_to_hold_handle = True then
			status_message := 'initialization';
			Y3:=True; //режим пружины
			Y6:=false; //верхний сжат
			Y7:=false; //нижний разжат
			
			liftmech.cmd := LIFT_CMD#stop;
			start_init_to_hold_handle := false;
			init_to_hold_state:=1;
		end_if;
		
	1 : //Подъем верхнего замка на 80 мм
			Y1:=False;
			Y2:=False;
			liftmech.cmd := LIFT_CMD#expand;
			
			Y6:=false; //верхний сжат
			Y7:=true; //нижний сзжат
			
			init_to_hold_state:=2;
	2:;
		if (liftmech.min_dist >= 850) then ;  
			
			liftmech.cmd := LIFT_CMD#stop;
			Y3:=false; //режим пружины отменить
			init_to_hold_state:=3;
		else
			status_message := 'Waiting for lift mech expand up to 850 mm';
		end_if;
		
	3:;
		status_message := 'Cycle comlete';
		init_to_hold_state:=99;

	
	99 :; // Processing after normal end
	  //up_frame_cycle_state:=INT#0;
	98 :; // Processing after error end.
		//
END_CASE;
