StructuredTextModel.Text:
;


CASE down_frame_cycle_state OF
	0 : 
		if start_down_frame_cycle_handle = True then
			Y3:=True; //режим пружины
			Y6:=true; //верхний расжат
			Y7:=false; //нижний разжат
			
			liftmech.cmd := LIFT_CMD#stop;
			down_frame_cycle_state:=1;
			last_max_diff:= 50;
			err_cnt:=0;
		end_if;
		
	1 : 

			if SQ5 and not SQ6 and
			SQ7 and not SQ8 and
			SQ13 and not SQ14 and
			SQ15 and not SQ16 and
			SQ21 and not SQ22 and
			SQ23 and not SQ24 and
			SQ29 and not SQ30 and
			SQ31 and not SQ32 then;
	
				Y1:=False;
				Y2:=False;
				liftmech.cmd := LIFT_CMD#expand;
				down_frame_cycle_state:=2;
			end_if;
	2:;
		if liftmech.max_dist>840 then;
			liftmech.cmd := LIFT_CMD#stop;
			down_frame_cycle_state:=98;
		end_if;
		if SQ5 and SQ6 and
			SQ7 and SQ8 and
			SQ13 and SQ14 and
			SQ15 and SQ16 and
			SQ21 and SQ22 and
			SQ23 and SQ24 and
			SQ29 and SQ30 and
			SQ31 and SQ32 
			then;
			
			Y3:=True; //режим пружины
			Y6:=false; //верхний сжат
			Y7:=false; //нижний разжат

			down_frame_cycle_state:=3;
		end_if;
		
	3:;	
		if (liftmech.state = LIFT_STATE#expand_end) then ;  
			
			
			Y3:=True; //режим пружины
			Y6:=false; //верхний сжат
			Y7:=true; //нижний сжат
			
			liftmech.cmd := LIFT_CMD#reduce;
			down_frame_cycle_state:=4;
		end_if;

	4:;
		
		if (liftmech.state = LIFT_STATE#reduce_end) then ;  	
			
			liftmech.cmd := LIFT_CMD#expand;
			down_frame_cycle_state:=6;
		end_if;

		
	6:;
		if (liftmech.min_dist >= 820) then ;  
			Y3:=True; //режим пружины
			Y6:=false; //верхний сжат
			Y7:=false; //нижний расжат
			
			liftmech.cmd := LIFT_CMD#stop;
			down_frame_cycle_state:=61;
		end_if;
	61:;
			if SQ2 and not SQ1 and
			SQ4 and not SQ3 and
			SQ10 and not SQ9 and
			SQ12 and not SQ11 and
			SQ18 and not SQ17 and
			SQ20 and not SQ19 and
			SQ26 and not SQ25 and
			SQ28 and not SQ27 then;
				down_frame_cycle_state:=62;
				liftmech.cmd := LIFT_CMD#reduce;
			end_if;
	62:;
		if (liftmech.state = LIFT_STATE#reduce_end) then ;  	
			
			down_frame_cycle_state:=7;

		end_if;	
	7:;
			Y3:=false; //режим пружины отменить
			liftmech.cmd := LIFT_CMD#stop;
			down_frame_cycle_state:=99;
	99 :; // Processing after normal end

	98 :; // Processing after error end.
		//
END_CASE;

start_down_frame_cycle_handle := false;
