StructuredTextModel.Text:
;
// Цикл подъема верхней рамы на до заданого давления.
// запуск из положения подперт замками, когда механизм немного разжат, замки смотрят вверх.


CASE link_mounting_to_init_state OF
	0 : // Начальное положение домкрата
		if start_link_mounting_to_init_handle = True then
			status_message := 'initialization';
			Y3:=True; //режим пружины
			Y6 :=false; //верхний сжат
			Y7:=false; //нижний расжат
			liftmech.cmd := LIFT_CMD#stop;
			start_link_mounting_to_init_handle := false;
			link_mounting_to_init_state:=1;
		end_if;
		
	1 : //Спуск верхнего замка до упора
			Y1:=False;
			Y2:=False;
			liftmech.cmd := LIFT_CMD#reduce;
			link_mounting_to_init_state:=2;
	2:;
		status_message := 'waiting for liftmech reduce end';
		if liftmech.state = LIFT_STATE#reduce_end then ; 
			liftmech.cmd:=LIFT_CMD#stop;
			link_mounting_to_init_state:=5;
		end_if;
		
	5:;
		Y3:=false; //режим пружины отменить
		status_message := 'Cycle finish succesfully';
		link_mounting_to_init_state:=99;
		Y1:=True;
		Y2:=True;

	6:;
	
	99 :; // Processing after normal end
	98 :; // Processing after error end.
		//
END_CASE;
