CASE up_from_upcrcyc_to_init_state OF
	0 :
		if start_from_upcrcyc_to_init_handle = True then
			status_message := 'initialisation';
			Y3:=True; //режим пружины
			Y6:=true; //верхний разжат
			Y7:=true; //нижний сжат
																		//|.          
																		//|.  ///     
																		//|. /// -----
																		//|.///       
																		//|.          
																		//|.          
																		//|.  ///     
																		//|. /// -----
																		//|.///       
																		//|.          

			liftmech.cmd := LIFT_CMD#stop;
			
			start_from_upcrcyc_to_init_handle := false;
			up_from_upcrcyc_to_init_state:=1;
		end_if;
		
	1 : 
			Y3:=True; //режим пружины
			Y6:=false; //верхний сжат
			Y7:=false; //нижний разжат
																		//|.          
																		//|.\\\       
																		//|. \\\ -----
																		//|.  \\\     
																		//|.          
																		//|.          
																		//|.\\\       
																		//|. \\\ -----
																		//|.  \\\     
																		//|.          


			Y1:=False;
			Y2:=False;

			up_from_upcrcyc_to_init_state:=10;
			
	10:;

	if 	(not(SQ1 or
			SQ3 or
			SQ9 or
			SQ11 or
			SQ17 or
			SQ19 or
			SQ25 or
			SQ27) )and
			( (SQ2 and	SQ4 and	SQ10 and SQ12 and	SQ18 and SQ20 and SQ26 and SQ28)) then;
				
			liftmech.cmd := LIFT_CMD#expand;
			
			up_from_upcrcyc_to_init_state:=2;
			end_if;
	2:;
		if 	(SQ6 and
			SQ8 and
			SQ14 and
			SQ16 and
			SQ22 and
			SQ24 and
			SQ30 and
			SQ32) and
			(not (SQ5 or	SQ7 or	SQ13 or SQ15 or	SQ21 or SQ23 or SQ29 or SQ31))
			then;
			
			Y3:=True; //режим пружины
			Y6:=false; //верхний сжат
			Y7:=true; //нижний cзжат			
			
			liftmech.cmd := LIFT_CMD#stop;

			up_from_upcrcyc_to_init_state:=21;
		end_if;
	21:;
		if liftmech.state = LIFT_STATE#holding then ; 
			liftmech.cmd := LIFT_CMD#reduce;
			up_from_upcrcyc_to_init_state:=3;
		end_if;
			
	3:;
		if ( liftmech.state = LIFT_STATE#reduce_end) then ;  	
			liftmech.cmd := LIFT_CMD#expand;
			up_from_upcrcyc_to_init_state:=4;
		end_if;
		
		
	4:;
		up_from_upcrcyc_to_init_state:=5;

	5:;
		if 	(SQ1 and
			SQ3 and
			SQ9 and
			SQ11 and
			SQ17 and
			SQ19 and
			SQ25 and
			SQ27) and
			(not (SQ2 or	SQ4 or	SQ10 or SQ12 or	SQ18 or SQ20 or SQ26 or SQ28))
			then;
			Y3:=True; //режим пружины
			Y6:=false; //верхний сжат
			Y7:=false; //нижний расжат		
			liftmech.cmd := LIFT_CMD#stop;
			
		
			up_from_upcrcyc_to_init_state:=51;
		end_if;
	51:;
		if liftmech.state = LIFT_STATE#holding then ; 
			liftmech.cmd := LIFT_CMD#reduce;
			up_from_upcrcyc_to_init_state:=6;
		end_if;
		
	6:;
		if (liftmech.state = LIFT_STATE#reduce_end) then ;  	
				Y3:=false; //режим пружины отменить
				liftmech.cmd := LIFT_CMD#stop;
				up_from_upcrcyc_to_init_state:=99;
		end_if;
	99 :; // Processing after normal end
	  //up_frame_cycle_state:=INT#0;
	98 :; // Processing after error end.
		//
END_CASE;
