StructuredTextModel.Text:
;
CASE from_hold_to_init_state OF
	0 : 
		if start_from_hold_to_init_handle = True then
			status_message := 'initialisation';
			
			Y3:=True; //режим пружины
			Y6:=false; //верхний сжат
			Y7:=false; //нижний разжат
			
			liftmech.cmd := LIFT_CMD#stop;
			start_from_hold_to_init_handle := false;
			from_hold_to_init_state:=1;
		end_if;
		
	1 :;
			Y1:=False;
			Y2:=False;
			liftmech.cmd := LIFT_CMD#reduce;
			from_hold_to_init_state:=2;
			status_message := 'Reducing lift mech';

	2:;
		if (liftmech.state = LIFT_STATE#reduce_end) then ;  	
			
			liftmech.cmd := LIFT_CMD#stop;
			Y3:=false; //режим пружины отменить
			from_hold_to_init_state:=3;
		end_if;
		
	3:;
		from_hold_to_init_state:=99;
		status_message := 'Cycle complete';


	
	99 :; // Processing after normal end
	  //up_frame_cycle_state:=INT#0;
	98 :; // Processing after error end.
		//
END_CASE;
