StructuredTextModel.Text:
;
// Цикл подъема верхней рамы на до заданого давления.
// запуск из положения init, когда механизм сжат, замки смотрят вверх.


CASE lift_up_frame_by_pressure_state OF
	0 : // Начальное положение домкрата
		if start_lift_up_frame_by_pressure_handle = True then
			status_message := 'initialization';
			Y3:=True; //режим пружины
			Y6 :=false; //верхний сжат
			Y7:=false; //нижний расжат
			liftmech.cmd := LIFT_CMD#stop;
			start_lift_up_frame_by_pressure_handle := false;
			lift_up_frame_by_pressure_state:=1;
		end_if;
		
	1 : //Подъем верхнего замка на до упора в раму и до давления в 65 атм
			Y1:=False;
			Y2:=False;
			liftmech.cmd := LIFT_CMD#expand;
			lift_up_frame_by_pressure_state:=2;
	2:;
		status_message := 'waiting for pressure reach target';
		if BP1 > 65.0 then ; 
			liftmech.cmd:=LIFT_CMD#stop;
			lift_up_frame_by_pressure_state:=5;
		end_if;
		if liftmech.max_dist > 840 then ;
			lift_up_frame_by_pressure_state:=98;
			status_message := 'Fail. Liftmech expands too large';
		end_if;			
		
	5:;
		if (SQ_DW=16#aaaaaaaa ) then;
			Y3:=false; //режим пружины отменить
			status_message := 'Cycle finish succesfully';
			lift_up_frame_by_pressure_state:=99;
		else
			status_message := 'Fail. Locks are in wrong position';
		end_if;
		Y1:=True;
		Y2:=True;

	6:;
	
	99 :; // Processing after normal end
	98 :; // Processing after error end.
		//
END_CASE;
